// Flattened output for Default Data Loader
return items.map(({ json }) => {
  const text = (json.markdown || json.text || '').trim();
  const firstLine = (text.split('\n').find(Boolean) || '').replace(/^#+\s*/, '').slice(0,120);

  return {
    json: {
      text,                         // your main text
      title: firstLine || '',        // each metadata item now top-level
      url: json.url || '',
      type: 'webpage',
      section: json.section || 'General',
      created_at: new Date().toISOString()
    }
  };
});
