const universities = [
  { name: 'NUS', data: $('Scrape NUS Requirements').item.json.data },
  { name: 'NTU', data: $('Scrape NTU Requirements').item.json.data },
  { name: 'SIT', data: $('Scrape SIT Requirements').item.json.data },
  { name: 'SUTD', data: $('Scrape SUTD Requirements').item.json.data },
  { name: 'SMU', data: $('Scrape SMU Requirements').item.json.data }
];

const userInput = $('Webhook').item.json.body;

return universities.map(uni => ({
  json: {
    university: uni.name,
    htmlContent: uni.data,
    userGrades: userInput.grades || '',
    userSubjects: userInput.subjects || '',
    userQualifications: userInput.qualifications || ''
  }
}));

const analyses = $input.all().map(item => item.json);

const eligible = analyses.filter(a => a.eligibilityStatus === 'ELIGIBLE');
const borderline = analyses.filter(a => a.eligibilityStatus === 'BORDERLINE');
const notEligible = analyses.filter(a => a.eligibilityStatus === 'NOT ELIGIBLE');

// Determine if appeals are needed
const needsAppeal = (borderline.length > 0 || notEligible.length > 0) && eligible.length === 0;

const summary = {
  totalUniversitiesAnalyzed: analyses.length,
  eligibleUniversities: eligible.map(u => u.university),
  borderlineUniversities: borderline.map(u => u.university),
  notEligibleUniversities: notEligible.map(u => u.university),
  detailedAnalysis: analyses,
  needsAppeal: needsAppeal,
  overallRecommendation: eligible.length > 0 
    ? `You are eligible for ${eligible.length} university/universities. Focus your applications there.`
    : borderline.length > 0
    ? `You have borderline eligibility for ${borderline.length} university/universities. Consider improving your profile or applying with strong supporting documents. Appeal letters will be generated.`
    : `Unfortunately, you may not meet the minimum requirements for these universities. Appeal letters will be generated to request reconsideration.`
};

return [{ json: summary }];

