const appeals = $input.first().json;
const userEmail = $('Webhook').item.json.body.email || 'applicant@example.com';
const userName = $('Webhook').item.json.body.name || 'Applicant';

return appeals.map(appeal => ({
  json: {
    university: appeal.university,
    to: getUniversityEmail(appeal.university),
    from: userEmail,
    subject: appeal.subject,
    body: appeal.letterBody,
    keyStrengths: appeal.keyStrengths,
    attachments: appeal.recommendedAttachments,
    applicantName: userName,
    fullAppealPackage: `To: Admissions Office, ${appeal.university}\nFrom: ${userName} (${userEmail})\n\nSubject: ${appeal.subject}\n\n${appeal.letterBody}\n\nKey Strengths to Highlight:\n${appeal.keyStrengths.map((s, i) => `${i + 1}. ${s}`).join('\n')}\n\nRecommended Supporting Documents:\n${appeal.recommendedAttachments.map((a, i) => `${i + 1}. ${a}`).join('\n')}`
  }
}));

function getUniversityEmail(uni) {
  const emails = {
    'NUS': 'admissions@nus.edu.sg',
    'NTU': 'admissions@ntu.edu.sg',
    'SIT': 'admissions@singaporetech.edu.sg',
    'SUTD': 'admissions@sutd.edu.sg',
    'SMU': 'admissions@smu.edu.sg'
  };
  return emails[uni] || 'admissions@university.edu.sg';
}
