const checks = $input.first().json.output.checks.map((el) => {
    return `${el.id} - Status: ${el.status} - Score: ${el.score} - ${el.details}`
})

const issues = $input.first().json.output.issues.map((el) => {
    return `${el.id} - Severity: ${el.severity} - Priority: ${el.priority} - ${el.location}`
})

const recommendations = $input.first().json.output.recommendations.map((el) => {
    return `${el.id} - Severity: ${el.severity} - Priority: ${el.priority} - ${el.instruction}`
})

return {
  time: $input.first().json.output.fetched_at,
  url: $input.first().json.output.final_url,
  checks: checks.join("\n\n"),
  issues: issues.join("\n\n"),
  recommendations: recommendations.join("\n\n")
}
