  } else if (accountsInfo.label === 'SOON' || confirmationInfo.label === 'SOON') {
    statusIcon = 'üü°';
    statusText = 'SOON';
  }

  const yesUrl = `${webhookBaseUrl}?company_number=${company.company_number}&company_name=${encodeURIComponent(company.company_name)}&confirmation_submitted=yes`;
  const noUrl = `${webhookBaseUrl}?company_number=${company.company_number}&company_name=${encodeURIComponent(company.company_name)}&confirmation_submitted=no`;

  emailBody += '<tr>';
  emailBody += `<td><div class="company-name">${company.company_name}</div></td>`;
  emailBody += `<td style="font-family: monospace;">${company.company_number}</td>`;

  emailBody += '<td>';
