  emailBody += `<a href="${noUrl}" class="btn btn-no">‚úó No</a>`;
  emailBody += '</td>';

  emailBody += '</tr>';
});

emailBody += '</tbody></table>';
emailBody += '<div style="margin-top: 20px; padding: 15px; background: white; border-radius: 8px;">';
emailBody += '<p style="color: #666; font-size: 13px;">üî¥ Red = Overdue | üü† Orange = Due within 7 days | üü° Yellow = Due within 30 days | ‚úÖ Green = OK</p>';
emailBody += '</div>';
emailBody += '</body></html>';

let subject = `Companies House Deadlines - ${companies.length} Companies`;

return [{
  json: {
    emailBody: emailBody,
    subject: subject,
    totalCompanies: companies.length
  }
}];

