// Get data from previous nodes
const inputData = $input.item.json;

// Extract webhook body data
const webhookData = inputData.body || inputData;

// Generate unique Press ID
const timestamp = Date.now();
const randomStr = Math.random().toString(36).substring(2, 8).toUpperCase();
const pressId = `PRESS-${randomStr}-${timestamp}`;

// Your event verification portal URL
const baseUrl = 'https://your-event-portal.com';
const verificationUrl = `${baseUrl}/verify?id=${pressId}`;

// Generate QR code using free API
const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=300x300&format=png&data=${encodeURIComponent(verificationUrl)}`;

// Merge everything together
return {
  json: {
    // Preserve webhook body data
    body: webhookData,
    // Add validation data
    email: inputData.email || webhookData.email,
    valid: inputData.valid,
    details: inputData.details,
    // Add new press pass data
    press_id: pressId,
    verification_url: verificationUrl,
    qr_code_url: qrCodeUrl,
    issued_at: new Date().toISOString(),
    issued_date: new Date().toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    }),
    valid_until: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    })
  }
};

