// Get meeting start time from original Cal.com data
const meetingStartTime = $('Extract prospect').item.json.meeting_start_time;

// Calculate days until meeting
const meetingDate = new Date(meetingStartTime);
const today = new Date();
const diffTime = meetingDate - today;
const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

// Get all previous data
const prospectData = $('Extract prospect').item.json;
const aiData = $input.item.json;

return {
  json: {
    // Original prospect data
    attendee_email: prospectData.attendee_email,
    attendee_name: prospectData.attendee_name,
    meeting_start_time: prospectData.meeting_start_time,
    meeting_title: prospectData.meeting_title,
    email_domain: prospectData.email_domain,
    company_name: prospectData.company_name,  // ‚Üê ADDED
    website_url: prospectData.website_url,

    // AI analysis data
    has_website_data: aiData.has_website_data,
    business_type: aiData.business_type,
    tech_stack: aiData.tech_stack,
    likely_pain_points: aiData.likely_pain_points,
    personalization_hook: aiData.personalization_hook,

    // Days calculation
    days_until_meeting: diffDays
  }
};

// Get attendee email from Cal.com webhook
const attendeeEmail = $input.item.json.attendees[0].email;
const attendeeName = $input.item.json.attendees[0].name;
const meetingStartTime = $input.item.json.startTime;
const meetingTitle = $input.item.json.eventTitle;

// Get company name from custom field (if provided)
const companyName = $input.item.json.responses['Company-Name']?.value || null;

// Extract domain from email
const domain = attendeeEmail.split('@')[1];

// List of consumer email providers
const consumerDomains = [
  'gmail.com', 'yahoo.com', 'outlook.com', 'hotmail.com',
  'aol.com', 'icloud.com', 'protonmail.com', 'mail.com',
  'live.com', 'msn.com', 'ymail.com', 'googlemail.com'
];

// Check if it's a company email
const isCompanyEmail = !consumerDomains.includes(domain.toLowerCase());

// Build website URL - use company name if provided and consumer email, otherwise use domain
let websiteUrl = null;
if (companyName && !isCompanyEmail) {
  // They gave us company name but have Gmail - try to build URL from company name
  websiteUrl = `https://${companyName.toLowerCase().replace(/\s+/g, '')}.com`;
} else if (isCompanyEmail) {
  // Company email - use domain
  websiteUrl = `https://${domain}`;
}

return {
  json: {
    attendee_email: attendeeEmail,
    attendee_name: attendeeName,
    meeting_start_time: meetingStartTime,
    meeting_title: meetingTitle,
    email_domain: domain,
    company_name: companyName,
    is_company_email: isCompanyEmail,
    website_url: websiteUrl
  }
};

