// n8n Code Node - Instagram Data Formatter
// Access input data
const inputData = $input.all();

// Process and format the data
const formattedItems = [];

for (const item of inputData) {
  const rawData = item.json;

  // Check if data array exists
  if (rawData.data && Array.isArray(rawData.data)) {

    // Process each post
    for (const post of rawData.data) {
      const formattedPost = {
        // Post Basic Info
        post_id: post.id,
        post_url: post.permalink,
        timestamp: post.timestamp,
        posted_date: new Date(post.timestamp).toLocaleDateString(),
        posted_time: new Date(post.timestamp).toLocaleTimeString(),

        // Content Info
        caption: post.caption || '',
        media_type: post.media_type,
        media_url: post.media_url,

        // Engagement Metrics
        likes: post.like_count || 0,
        comments_count: post.comments_count || 0,
        total_engagement: (post.like_count || 0) + (post.comments_count || 0),

        // Comments Details
        comments: [],
        has_comments: post.comments && post.comments.data && post.comments.data.length > 0
      };

      // Process comments if they exist
      if (post.comments && post.comments.data && Array.isArray(post.comments.data)) {
        formattedPost.comments = post.comments.data.map(comment => ({
          comment_id: comment.id,
          username: comment.username,
          text: comment.text,
          timestamp: comment.timestamp,
          commented_date: new Date(comment.timestamp).toLocaleDateString(),
          commented_time: new Date(comment.timestamp).toLocaleTimeString(),
          likes: comment.like_count || 0
        }));
      }

      // Add the formatted post to results
      formattedItems.push({ json: formattedPost });
    }
  }
}

// Return formatted data
return formattedItems;

// n8n Code Node - Format Instagram Analytics for Google Sheets
// This flattens the nested analytics data into sheet-friendly rows

const items = $input.all();
const sheetRows = [];

for (const item of items) {
  const data = item.json.output || item.json;

  // Process each post in the analytics
  if (data.posts && Array.isArray(data.posts)) {
    data.posts.forEach(post => {
      const postRow = {
        // Summary Information
        totalPostsAnalyzed: data.summary?.totalPostsAnalyzed || 0,
        averageEngagement: data.summary?.averageEngagement || 0,
        topPerformingPostId: data.summary?.topPerformingPostId || '',
        topPerformanceReason: data.summary?.topPerformanceReason || '',

        // Post Basic Information
        postId: post.post_id || '',
        postUrl: post.post_url || '',
        caption: post.caption || '',

        // Engagement Metrics
        likes: post.likes || 0,
        commentsCount: post.comments_count || 0,
        totalEngagement: post.total_engagement || 0,
        engagementScore: post.engagementScore || 0,

        // Performance Assessment
        performanceLabel: post.performanceLabel || '',
        insight: post.insight || '',
        recommendation: post.recommendation || '',

        // Comment Sentiment
        sentimentScore: post.commentSentiment?.averageScore || 0,
        sentimentLabel: post.commentSentiment?.label || '',
        positiveCommentsCount: post.commentSentiment?.positiveComments?.length || 0,
        negativeCommentsCount: post.commentSentiment?.negativeComments?.length || 0,

        // Positive Comments (comma-separated)
        positiveComments: post.commentSentiment?.positiveComments?.join(' | ') || '',

        // Negative Comments (comma-separated)
        negativeComments: post.commentSentiment?.negativeComments?.join(' | ') || '',

        // Alert Flags
        needsAttention: post.commentSentiment?.label === 'Negative' || 
                        post.performanceLabel === 'Poor',
        isTopPerformer: post.post_id === data.summary?.topPerformingPostId,

        // Overall Insights (from summary)
        contentPatterns: data.overallInsights?.contentPatterns?.join(' | ') || '',
        engagementInsights: data.overallInsights?.engagementInsights?.join(' | ') || '',
        recommendations: data.overallInsights?.recommendations?.join(' | ') || '',

        // Timestamp
        analyzedAt: new Date().toISOString(),
        analyzedDate: new Date().toLocaleDateString(),
        analyzedTime: new Date().toLocaleTimeString()
      };

      sheetRows.push(postRow);
    });
  }
}

// Return formatted data for Google Sheets
return sheetRows.map(row => ({ json: row }));

