// Get the input content
const content = $input.first().json.output || $input.item.json.content || $input.item.json.message || '';

const MAX_LENGTH = 1950;

function splitIntoChunks(text, maxLength) {
  // If content is within limit, return as single item
  if (text.length <= maxLength) {
    return [text];
  }

  const chunks = [];
  let start = 0;

  while (start < text.length) {
    let end = start + maxLength;

    // If we're not at the end, try to break at a good spot
    if (end < text.length) {
      // Try to break at newline
      const lastNewline = text.lastIndexOf('\n', end);
      if (lastNewline > start) {
        end = lastNewline;
      } else {
        // Try to break at space
        const lastSpace = text.lastIndexOf(' ', end);
        if (lastSpace > start) {
          end = lastSpace;
        }
      }
    }

    chunks.push(text.substring(start, end).trim());
    start = end + 1;
  }

  return chunks;
}

// Split the content into chunks
const chunks = splitIntoChunks(content, MAX_LENGTH);

// Return as array of items for the Loop node
return chunks.map(chunk => ({
  json: {
    message: chunk
  }
}));
