// This code processes the results from all BigQuery nodes and creates a summary message.

// Statically define the names of the BigQuery nodes in the exact order they are connected to the Merge node(s).
const nodeNames = [
  'BQ - ga4_transaction_items',
  'BQ - ga4_data_session_channel_group',
  'BQ - ga4_data_session_source_campaign_medium',
  'BQ - ga4_data_country_language_city',
  'BQ - ga4_data_item_name',
  'BQ - ga4_data_browser_os_device',
  'BQ - ga4_data_first_user_source_medium',
  'BQ - ga4_data_first_user_channel_group',
  'BQ - ga4_ads_data',
  'BQ - ga4_all_metrics_data',
  'BQ - ga4_event_metrics_data',
  'BQ - ga4_page_location_data',
  'BQ - ga4_landing_page_data'
];

const successfulNodes = [];
const failedNodes = [];

// Loop through all the input items from the previous nodes.
for (const [index, item] of $input.all().entries()) {
  const nodeName = nodeNames[index] || `Unknown Node (Input ${index + 1})`;

  if (item.json.error) {
    // Escape underscores in the node name to prevent Markdown parsing errors
    const escapedNodeName = nodeName.replace(/_/g, '\\_');
    failedNodes.push(escapedNodeName);
  } else {
    successfulNodes.push(nodeName);
  }
}

// Start building the message string with the success count.
let message = `GA4 to BigQuery Workflow Status: ${successfulNodes.length}/13 Successful ✅\n\n`;

// Add a section for failed nodes only if there are any.
if (failedNodes.length > 0) {
  message += "❌ **Failed Nodes:**\n" + failedNodes.join("\n");
}

// Return the final message to be used in the Telegram node.
return [{ json: { message: message } }];
